import React, { useEffect, useState } from "react";

import "../root.css";

import logoReact from "../images/logo.png";
import {
  Form,
  NavLink,
  Outlet,
  useLoaderData,
  useNavigate,
  useNavigation,
  useSubmit,
} from "react-router-dom";

import { useSelector } from "react-redux";
const Root = () => {
  const contactsSlice = useSelector((state) => state.contact);

  const [search, getSearch] = useState("");

  const contacts = useLoaderData();
  const navigate = useNavigate();
  const navigation = useNavigation();
  const submit = useSubmit();

  const createContact = () => {
    navigate(`contacts/create-new-contact`);
  };

  const changeSearch = (e) => {
    const value = e.target.value;
    getSearch(value);
  };

  const getContact = async (search) => {
    const res = await fetch(`http://localhost:5000/contacts/`);
    const data = await res.json();
    const contacts = data.filter((contact) => {
      return contact.first.includes(search) || contact.last.includes(search);
    });
    return contacts;
  };

  useEffect(() => {
    getContact(search);
  }, [search]);

  return (
    <div className={"content"}>
      <div className={"sidebar"}>
        <div className={"search"}>
          <Form className={"searchForm"} role="search">
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              id="search"
              aria-label="Search contacts"
              placeholder="Search"
              type="search"
              name="search"
              value={search}
              onChange={changeSearch}
            />
          </Form>
          <div className={"submitForm"}>
            <button
              type="submit"
              className="custom-btn"
              onClick={createContact}
            >
              New
            </button>
          </div>
        </div>
        <nav>
          <ul>
            {contacts.length ? (
              contacts.map((contact) => {
                return (
                  <li key={contact.id}>
                    <NavLink to={`/contacts/${contact.id}`}>
                      {contact.first} {contact.last}
                    </NavLink>
                  </li>
                );
              })
            ) : (
              <p>No Name</p>
            )}
          </ul>
        </nav>
        <div className={"logo"}>
          <img src={logoReact} alt="logo" />
          <p>React Router Contacts</p>
        </div>
      </div>
      <div
        id={"detail"}
        className={navigation.state === "loading" ? "loading" : ""}
      >
        <Outlet />
      </div>
    </div>
  );
};

export default Root;

