import React from "react";

import styles from "../root.module.css";

import logoReact from "../images/logo.png";
import { NavLink, Outlet, useLoaderData } from "react-router-dom";

const Root = () => {
  const contacts = useLoaderData();

  const {
    content,
    sidebar,
    search,
    searchForm,
    searchSpinner,
    srOnly,
    submitForm,
    logo,
    detail,
  } = styles;
  return (
    <div className={content}>
      <div className={sidebar}>
        <div className={search}>
          <form className={searchForm} role="search">
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              id="q"
              aria-label="Search contacts"
              placeholder="Search"
              type="search"
              name="q"
            />
          </form>
          <form className={submitForm} method="post">
            <button type="submit">New</button>
          </form>
        </div>
        <nav>
          <ul>
            {contacts.length ? (
              contacts.map((contact) => {
                return (
                  <li key={contact.id}>
                    <NavLink className="new" to={`/contacts/${contact.id}`}>
                      {contact.first} {contact.last}
                    </NavLink>
                  </li>
                );
              })
            ) : (
              <p>No Contacts</p>
            )}
          </ul>
        </nav>
        <div className={logo}>
          <img src={logoReact} alt="logo" />
          <p>React Router Contacts</p>
        </div>
      </div>
      <div className={detail}>
        <Outlet />
      </div>
    </div>
  );
};

export default Root;
