import { createRoot } from 'react-dom/client';
import './fontawesome/css/all.min.css';
import './fontawesome/css/normalize.css';
import './framework.css'
import './index.css';
import './routes.css';
import App from './App';

import { createBrowserRouter, RouterProvider } from 'react-router-dom';


import Root from './routes/Root';
import ErrorPage from './ErrorPage';
import Contact, { /*This is a function */ deleteContact, loaderContact } from './routes/Contact';
import EditContact, { editContact } from './routes/EditContact';
import CreateContact, { createContact } from './routes/CreateContact';



const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    errorElement: <ErrorPage />,
    // loader: async () => {
    //   try {
    //     const res = await fetch(`http://localhost:5000/contacts/`);
    //     return res.json();
    //   } catch (error) {
    //     throw new Response("", {
    //       status: 404,
    //       statusText: "Not Found",
    //     });
    //   }
    // },
    children: [
      {
        path: "contacts/:contactID",
        element: <Contact />,
        loader: loaderContact,
        action: deleteContact,
      },
      {
        path: "contacts/:contactID/edit",
        element: <EditContact />,
        loader: loaderContact,
        action: editContact,
      },
      {
        path: "contacts/create-new-contact",
        element: <CreateContact />,
        action: createContact,
      },
    ]
  },
]);



const root = createRoot(document.getElementById('root'));
root.render(
  <RouterProvider router={router} />
);
