import React from "react";

import "../root.css";

import logoReact from "../images/logo.png";
import {
  Form,
  NavLink,
  Outlet,
  useActionData,
  useLoaderData,
  useNavigate,
  useNavigation,
} from "react-router-dom";

const Root = () => {
  const contacts = useLoaderData();
  const navigate = useNavigate();
  const navigation = useNavigation();
  const data = useActionData();

  console.log(data);

  const createContact = () => {
    navigate(`contacts/create-new-contact`);
  };

  return (
    <div className={"content"}>
      <div className={"sidebar"}>
        <div className={"search"}>
          <Form className={"searchForm"} role="search" action="/" method="post">
            <i className="fa-solid fa-magnifying-glass"></i>
            <input
              id="q"
              aria-label="Search contacts"
              placeholder="Search"
              type="search"
              name="search"
            />
          </Form>
          <div className={"submitForm"}>
            <button
              type="submit"
              className="custom-btn"
              onClick={createContact}
            >
              New
            </button>
          </div>
        </div>
        <nav>
          <ul>
            {contacts.length ? (
              contacts.map((contact) => {
                return (
                  <li key={contact.id}>
                    <NavLink to={`/contacts/${contact.id}`}>
                      {contact.first} {contact.last}
                    </NavLink>
                  </li>
                );
              })
            ) : (
              <p>No Name</p>
            )}
          </ul>
        </nav>
        <div className={"logo"}>
          <img src={logoReact} alt="logo" />
          <p>React Router Contacts</p>
        </div>
      </div>
      <div
        id={"detail"}
        className={navigation.state === "loading" ? "loading" : ""}
      >
        <Outlet />
      </div>
    </div>
  );
};

export default Root;

export async function getContact({ request }) {
  const url = new URL(request.url);
  const search = url.searchParams.get("search");
  console.log("URL", url);
  console.log("URL", search);

  return search;
}
