import React, { useEffect } from "react";

import "../root.css";

import logoReact from "../images/logo.png";
import {
  Form,
  NavLink,
  Outlet,
  useLoaderData,
  useNavigate,
  useNavigation,
  useSubmit,
} from "react-router-dom";

const Root = () => {
  const { contacts, s } = useLoaderData();
  const navigate = useNavigate();
  const navigation = useNavigation();
  const submit = useSubmit();

  const searching =
    navigation.location &&
    new URLSearchParams(navigation.location.search).has("search");

  const a = new URLSearchParams(navigation.location.search);

  console.log(a);

  const createContact = () => {
    navigate(`contacts/create-new-contact`);
  };

  useEffect(() => {
    document.getElementById("search").value = s;
  }, [s]);

  return (
    <div className={"content"}>
      <div className={"sidebar"}>
        <div className={"search"}>
          <Form
            className={"searchForm"}
            role="search"
            onChange={(event) => {
              submit(event.currentTarget);
            }}
          >
            {searching ? (
              <i className="fa-solid fa-rotate"></i>
            ) : (
              <i className="fa-solid fa-magnifying-glass"></i>
            )}
            <input
              id="search"
              // className={searching ? "loading" : ""}
              aria-label="Search contacts"
              placeholder="Search"
              type="search"
              name="search"
              defaultValue={s}
            />
            {/* <div id="search-spinner" aria-hidden hidden={!searching} /> */}
          </Form>
          <div className={"submitForm"}>
            <button
              type="submit"
              className="custom-btn"
              onClick={createContact}
            >
              New
            </button>
          </div>
        </div>
        <nav>
          <ul>
            {contacts.length ? (
              contacts.map((contact) => {
                return (
                  <li key={contact.id}>
                    <NavLink to={`/contacts/${contact.id}`}>
                      {contact.first} {contact.last}
                    </NavLink>
                  </li>
                );
              })
            ) : (
              <p>No Name</p>
            )}
          </ul>
        </nav>
        <div className={"logo"}>
          <img src={logoReact} alt="logo" />
          <p>React Router Contacts</p>
        </div>
      </div>
      <div
        id={"detail"}
        className={navigation.state === "loading" ? "loading" : ""}
      >
        <Outlet />
      </div>
    </div>
  );
};

export default Root;

export async function getContacts(search) {
  const res = await fetch(`http://localhost:5000/contacts/`);
  const contacts = await res.json();

  if (search) {
    const dataSearch = contacts.filter((contact) => {
      return contact.first.includes(search) || contact.last.includes(search);
    });
    return dataSearch;
  }

  return contacts;
}
